    def calculate_sector_area(self, angle):
        """
        Calculate the area of a sector based on the radius and angle.

        :param angle: angle of the sector, float
        :return: area of the sector, float

        >>> areaCalculator = AreaCalculator(2)
        >>> areaCalculator.calculate_sector_area(math.pi)
        6.283185307179586
        """
        if angle < 0:
            raise ValueError("Angle must be a non-negative number")

        # Calculate the area of the sector
        sector_area = (angle / (2 * math.pi)) * math.pi * self.radius ** 2
        return sector_area


    def calculate_annulus_area(self, inner_radius, outer_radius):
        return math.pi * (outer_radius ** 2 - inner_radius ** 2)

class ArgumentParser: 
    def __init__(self):
        """
        Initialize the fields.
        self.arguments is a dict that stores the args in a command line
        self.requried is a set that stores the required arguments
        self.types is a dict that stores type of every arguments.
        >>> parser.arguments
        {'key1': 'value1', 'option1': True}
        >>> parser.required
        {'arg1'}
        >>> parser.types
        {'arg1': 'type1'}
        """
        self.arguments = {}
        self.required = set()
        self.types = {}



    def parse_arguments(self, command_string):
        """
        Parses the given command line argument string and invoke _convert_type to stores the parsed result in specific type in the arguments dictionary.
        Checks for missing required arguments, if any, and returns False with the missing argument names, otherwise returns True.
        :param command_string: str, command line argument string, formatted like "python script.py --arg1=value1 -arg2 value2 --option1 -option2"
        :return tuple: (True, None) if parsing is successful, (False, missing_args) if parsing fails,
            where missing_args is a set of the missing argument names which are str.
        """
        # Extract the arguments from the command string
        args = re.findall(r'--(\w+)=(\S+)|-(\w+) (\S+)', command_string)

        # Process the arguments
        for arg in args:
            if arg[0]:  # Long option
                name = arg[0]
                value = arg[1]
            elif arg[2]:  # Short option
                name = arg[2]
                value = arg[3]
            else:
                raise ValueError("Invalid argument format")

            if name in self.arguments:
                self.arguments[name]['value'] = self._convert_type(value, self.arguments[name]['type'])
            else:
                raise ValueError(f"Unknown argument: {name}")

        # Check for missing required arguments
        missing_args = set()
        for name, details in self.arguments.items():
            if details['required'] and details['value'] is None:
                missing_args.add(name)

        if missing_args:
            return False, missing_args
        else:
            return True, None


    def get_argument(self, key):
        return self.arguments.get(key)

    def add_argument(self, arg, required=False, arg_type=str):
        if required:
            self.required.add(arg)
        self.types[arg] = arg_type

    def _convert_type(self, arg, value):
        try:
            return self.types[arg](value)
        except (ValueError, KeyError):
            return value

