        >>> parser.required
        {'arg1'}
        >>> parser.types
        {'arg1': 'int'}
        """
        # Check if the argument name is already in use
        if arg in self.types:
            raise ValueError(f"Argument '{arg}' already exists.")

        # Store the argument type in the types dictionary
        self.types[arg] = arg_type

        # If the argument is required, add it to the required set
        if required:
            self.required.add(arg)


