    def median(self, data):
        sorted_data = sorted(data)
        n = len(sorted_data)
        if n % 2 == 0:
            middle = n // 2
            return round((sorted_data[middle - 1] + sorted_data[middle]) / 2, 2)
        else:
            middle = n // 2
            return sorted_data[middle]

    def mode(self, data):
        """
        Calculate the mode of a set of data
        :param data: list, data list
        :return: list, the mode(s)
        >>> ds = DataStatistics()
        >>> ds.mode([2, 2, 3, 3, 4])
        [2, 3]
        """
        if not data:
            raise ValueError("The data list cannot be empty")

        # Create a dictionary to count the frequency of each element
        frequency = {}
        for item in data:
            if item in frequency:
                frequency[item] += 1
            else:
                frequency[item] = 1

        # Find the maximum frequency
        max_freq = max(frequency.values())

        # Collect all elements that have the maximum frequency
        modes = [item for item, freq in frequency.items() if freq == max_freq]

        return modes

