import numpy as np
class DataStatistics2: 
    def __init__(self, data):
        """
        Initialize Data List
        :param data:list
        """
        self.data = np.array(data)



    def get_sum(self):
        return np.sum(self.data)

    def get_min(self):
        return np.min(self.data)

    def get_max(self):
        return np.max(self.data)

    def get_variance(self):
        return round(np.var(self.data), 2)

    def get_std_deviation(self):
        return round(np.std(self.data), 2)

    def get_correlation(self):
        """
        Calculate the Pearson correlation coefficient.

        :return: float - Pearson correlation coefficient
        """
        if len(self.data) < 2:
            raise ValueError("Data must contain at least two points to calculate correlation.")

        n = len(self.data)
        mean_x = sum(self.data) / n
        mean_y = sum(self.data) / n

        numerator = sum((x - mean_x) * (y - mean_y) for x, y in zip(self.data, self.data))
        denominator = sum((x - mean_x) ** 2 for x in self.data) * sum((y - mean_y) ** 2 for y in self.data)

        if denominator == 0:
            raise ValueError("Data points are perfectly aligned, correlation is undefined.")

        correlation = numerator / (denominator ** 0.5)
        return correlation

class DecryptionUtils: 
    def __init__(self, key):
        """
        Initializes the decryption utility with a key.
        :param key: The key to use for decryption,str.
        """
        self.key = key



    def caesar_decipher(self, ciphertext, shift):
        plaintext = ""
        for char in ciphertext:
            if char.isalpha():
                if char.isupper():
                    ascii_offset = 65
                else:
                    ascii_offset = 97
                shifted_char = chr((ord(char) - ascii_offset - shift) % 26 + ascii_offset)
                plaintext += shifted_char
            else:
                plaintext += char
        return plaintext

    def vigenere_decipher(self, ciphertext):
        """
        Deciphers the given ciphertext using the Vigenere cipher
        :param ciphertext: The ciphertext to decipher, str.
        :return: The deciphered plaintext, str.
        """
        key = self.key
        plaintext = []
        key_length = len(key)
        key_as_int = [ord(i) for i in key]
        ciphertext_int = [ord(i) for i in ciphertext]

        for i in range(len(ciphertext_int)):
            value = (ciphertext_int[i] - key_as_int[i % key_length] + 26) % 26
            plaintext.append(chr(value + 65))

        return ''.join(plaintext)


    def rail_fence_decipher(self, encrypted_text, rails):
        fence = [['\n' for _ in range(len(encrypted_text))] for _ in range(rails)]
        direction = -1
        row, col = 0, 0

        for _ in range(len(encrypted_text)):
            if row == 0 or row == rails - 1:
                direction = -direction

            fence[row][col] = ''
            col += 1
            row += direction

        index = 0
        for i in range(rails):
            for j in range(len(encrypted_text)):
                if fence[i][j] == '':
                    fence[i][j] = encrypted_text[index]
                    index += 1

        plain_text = ''
        direction = -1
        row, col = 0, 0
        for _ in range(len(encrypted_text)):
            if row == 0 or row == rails - 1:
                direction = -direction

            plain_text += fence[row][col]
            col += 1
            row += direction

        return plain_text

