                elif self.map[row][col] == "G":
                    self.targets.append((row, col))
                    self.target_count += 1
                elif self.map[row][col] == "X":
                    self.boxes.append((row, col))

    def check_win(self):
        box_on_target_count = 0
        for box in self.boxes:
            if box in self.targets:
                box_on_target_count += 1
        if box_on_target_count == self.target_count:
            self.is_game_over = True
        return self.is_game_over

    def move(self, direction):
        """
        Move the player based on the specified direction and check if the game is won.
        :param direction: str, the direction of the player's movement. 
            It can be 'w', 's', 'a', or 'd' representing up, down, left, or right respectively.

        :return: True if the game is won, False otherwise.
        """
        directions = {'w': (-1, 0), 's': (1, 0), 'a': (0, -1), 'd': (0, 1)}
        dx, dy = directions.get(direction, (0, 0))

        player_x, player_y = self.player_pos
        new_player_x, new_player_y = player_x + dx, player_y + dy

        if not (0 <= new_player_x < len(self.map_data) and 0 <= new_player_y < len(self.map_data[0])):
            return False  # Move out of bounds

        if self.map_data[new_player_x][new_player_y] == '#':
            return False  # Move into wall

        if self.map_data[new_player_x][new_player_y] == 'X':
            box_x, box_y = self.box_pos
            new_box_x, new_box_y = box_x + dx, box_y + dy

            if not (0 <= new_box_x < len(self.map_data) and 0 <= new_box_y < len(self.map_data[0])):
                return False  # Box move out of bounds

            if self.map_data[new_box_x][new_box_y] == '#':
                return False  # Box move into wall

            if self.map_data[new_box_x][new_box_y] == 'X':
                return False  # Box move onto another box

            # Move the box
            self.map_data[box_x][box_y] = '.'
            self.map_data[new_box_x][new_box_y] = 'X'
            self.box_pos = (new_box_x, new_box_y)

        # Move the player
        self.map_data[player_x][player_y] = '.'
        self.map_data[new_player_x][new_player_y] = 'O'
        self.player_pos = (new_player_x, new_player_y)

        # Check if the game is won
        return self.box_pos == self.goal_pos

