        return query

    @staticmethod
    def update(table, data, where=None):
        """
        Generate the UPDATE SQL statement from the given parameters.
        :param table: str, the table that will be executed with UPDATE operation in the database
        :param data: dict, the key and value in SQL update statement
        :param where: dict, {key1: value1, key2: value2 ...}. The query condition.
        :return: str, the generated SQL UPDATE statement
        >>> SQLQueryBuilder.update('table1', {'name': 'Test2', 'age': 15}, where={'name': 'Test'})
        "UPDATE table1 SET name='Test2', age='15' WHERE name='Test'"
        """
        # Validate input types
        if not isinstance(table, str):
            raise TypeError("table must be a string")
        if not isinstance(data, dict):
            raise TypeError("data must be a dictionary")
        if where is not None and not isinstance(where, dict):
            raise TypeError("where must be a dictionary or None")

        # Build the SET part of the SQL statement
        set_clause = ', '.join([f"{key}='{value}'" for key, value in data.items()])

        # Build the WHERE part of the SQL statement
        where_clause = ' AND '.join([f"{key}='{value}'" for key, value in where.items()]) if where else None

        # Construct the final SQL statement
        sql_statement = f"UPDATE {table} SET {set_clause}"
        if where_clause:
            sql_statement += f" WHERE {where_clause}"

        return sql_statement

