        if c2.real == 0 and c2.imag == 0:
            raise ValueError("Division by zero is not allowed.")

        numerator_real = c1.real * c2.real + c1.imag * c2.imag
        numerator_imag = c1.imag * c2.real - c1.real * c2.imag
        denominator = c2.real**2 + c2.imag**2

        return complex(numerator_real / denominator, numerator_imag / denominator)

