class DataStatistics2: 
    def __init__(self, data):
        """
        Initialize Data List
        :param data:list
        """
        self.data = np.array(data)



    def get_sum(self):
        return np.sum(self.data)

    def get_min(self):
        return np.min(self.data)

    def get_max(self):
        return np.max(self.data)

    def get_variance(self):
        return round(np.var(self.data), 2)

    def get_std_deviation(self):
        return round(np.std(self.data), 2)

    def get_correlation(self):
        """
        Calculate correlation
        :return:float
        >>> ds2 = DataStatistics2([1, 2, 3, 4])
        >>> ds2.get_correlation()
        1.0
        """
        if len(self.data) < 2:
            raise ValueError("Correlation requires at least two data points.")

        # Calculate the mean of the data
        mean = np.mean(self.data)

        # Calculate the numerator and denominator for the correlation coefficient
        numerator = np.sum((self.data - mean) * (self.data - mean))
        denominator = np.sqrt(np.sum((self.data - mean) ** 2) * np.sum((self.data - mean) ** 2))

        # Calculate the correlation coefficient
        correlation = numerator / denominator

        return round(correlation, 2)

