class ArgumentParser: 
    def __init__(self):
        """
        Initialize the fields.
        self.arguments is a dict that stores the args in a command line
        self.requried is a set that stores the required arguments
        self.types is a dict that stores type of every arguments.
        >>> parser.arguments
        {'key1': 'value1', 'option1': True}
        >>> parser.required
        {'arg1'}
        >>> parser.types
        {'arg1': 'type1'}
        """
        self.arguments = {}
        self.required = set()
        self.types = {}



    def parse_arguments(self, command_string):
        """
        Parses the given command line argument string and invoke _convert_type to stores the parsed result in specific type in the arguments dictionary.
        Checks for missing required arguments, if any, and returns False with the missing argument names, otherwise returns True.
        :param command_string: str, command line argument string, formatted like "python script.py --arg1=value1 -arg2 value2 --option1 -option2"
        :return tuple: (True, None) if parsing is successful, (False, missing_args) if parsing fails,
            where missing_args is a set of the missing argument names which are str.
        """
        # Split the command string into individual arguments
        args = command_string.split()

        # Initialize a dictionary to store the parsed arguments
        parsed_args = {}

        # Iterate over the arguments
        i = 0
        while i < len(args):
            arg = args[i]

            # Check if the argument is a flag (e.g., --arg1)
            if arg.startswith('--'):
                # Get the argument name
                arg_name = arg[2:]

                # Check if the argument is required
                if arg_name in self.required:
                    # Check if the next argument is a value
                    if i + 1 < len(args) and not args[i + 1].startswith('-'):
                        # Get the value
                        value = args[i + 1]
                        # Convert the value to the specified type
                        parsed_args[arg_name] = self._convert_type(arg_name, value)
                        # Move to the next argument
                        i += 1
                    else:
                        # Missing value for a required argument
                        return False, {arg_name}

                # If the argument is not required, check if it has a value
                else:
                    # Check if the next argument is a value
                    if i + 1 < len(args) and not args[i + 1].startswith('-'):
                        # Get the value
                        value = args[i + 1]
                        # Convert the value to the specified type
                        parsed_args[arg_name] = self._convert_type(arg_name, value)
                        # Move to the next argument
                        i += 1
                    else:
                        # No value for an optional argument
                        parsed_args[arg_name] = None

            # Check if the argument is a short flag (e.g., -arg1)
            elif arg.startswith('-'):
                # Get the argument name
                arg_name = arg[1:]

                # Check if the argument is required
                if arg_name in self.required:
                    # Check if the next argument is a value
                    if i + 1 < len(args) and not args[i + 1].startswith('-'):
                        # Get the value
                        value = args[i + 1]
                        # Convert the value to the specified type
                        parsed_args[arg_name] = self._convert_type(arg_name, value)
                        # Move to the next argument
                        i += 1
                    else:
                        # Missing value for a required argument
                        return False, {arg_name}

                # If the argument is not required, check if it has a value
                else:
                    # Check if the next argument is a value
                    if i + 1 < len(args) and not args[i + 1].startswith('-'):
                        # Get the value
                        value = args[i + 1]
                        # Convert the value to the specified type
                        parsed_args[arg_name] = self._convert_type(arg_name, value)
                        # Move to the next argument
                        i += 1
                    else:
                        # No value for an optional argument
                        parsed_args[arg_name] = None

            # If the argument is not a flag, it's likely a positional argument
            else:
                # Add the positional argument to the parsed arguments
                parsed_args[arg] = None

            # Move to the next argument
            i += 1

        # Check for missing required arguments
        missing_args = self.required - set(parsed_args.keys())
        if missing_args:
            return False, missing_args

        # Update the arguments dictionary with the parsed arguments
        self.arguments.update(parsed_args)

        return True, None


    def get_argument(self, key):
        return self.arguments.get(key)

    def add_argument(self, arg, required=False, arg_type=str):
        if required:
            self.required.add(arg)
        self.types[arg] = arg_type

    def _convert_type(self, arg, value):
        try:
            return self.types[arg](value)
        except (ValueError, KeyError):
            return value

