class DataStatistics: 


    def mean(self, data):
        return round(sum(data) / len(data), 2)

    def median(self, data):
        sorted_data = sorted(data)
        n = len(sorted_data)
        if n % 2 == 0:
            middle = n // 2
            return round((sorted_data[middle - 1] + sorted_data[middle]) / 2, 2)
        else:
            middle = n // 2
            return sorted_data[middle]

    def mode(self, data):
        """
        Calculate the mode of a set of data
        :param data: list, data list
        :return: list, the mode(s)
        >>> ds = DataStatistics()
        >>> ds.mode([2, 2, 3, 3, 4])
        [2, 3]
        """
        if not data:
            return []

        # Dictionary to store the frequency of each element
        frequency = {}

        # Calculate the frequency of each element in the data
        for num in data:
            if num in frequency:
                frequency[num] += 1
            else:
                frequency[num] = 1

        # Find the maximum frequency
        max_frequency = max(frequency.values())

        # Collect all elements with the maximum frequency
        modes = [num for num, freq in frequency.items() if freq == max_frequency]

        return modes

