        return np.sum(self.data)

    def get_min(self):
        return np.min(self.data)

    def get_max(self):
        return np.max(self.data)

    def get_variance(self):
        return round(np.var(self.data), 2)

    def get_std_deviation(self):
        return round(np.std(self.data), 2)

    def get_correlation(self):
        """
        Calculate correlation
        :return: float
        >>> ds2 = DataStatistics2([1, 2, 3, 4])
        >>> ds2.get_correlation()
        1.0
        """
        if len(self.data) < 2:
            raise ValueError("At least two data points are required to calculate correlation.")

        # Calculate the mean of the data
        mean = np.mean(self.data)

        # Calculate the numerator of the correlation formula
        numerator = sum((x - mean) * (y - mean) for x, y in zip(self.data, self.data))

        # Calculate the denominator of the correlation formula
        denominator = np.sqrt(sum((x - mean) ** 2 for x in self.data)) * np.sqrt(sum((y - mean) ** 2 for y in self.data))

        # Calculate the correlation coefficient
        correlation = numerator / denominator

        return correlation

