from PIL import Image, ImageEnhance, ImageChops
class ImageProcessor: 
    def __init__(self):
        """
        Initialize self.image
        """
        self.image = None



    def load_image(self, image_path):
        self.image = Image.open(image_path)

    def save_image(self, save_path):
        if self.image:
            self.image.save(save_path)

    def resize_image(self, width, height):
        if self.image:
            self.image = self.image.resize((width, height))

    def rotate_image(self, degrees):
        if self.image:
            self.image = self.image.rotate(degrees)

    def adjust_brightness(self, factor):
        """
        Adjust the brightness of the image.
        :param factor: float, brightness factor. A factor of 0.0 gives a black image. A factor of 1.0 gives the original image.
        """
        if self.image is None:
            raise ValueError("Image not loaded. Please load an image first using load_image method.")

        # Convert the image to RGB mode if it's not already
        if self.image.mode != 'RGB':
            self.image = self.image.convert('RGB')

        # Adjust brightness
        adjusted_image = ImageEnhance.Brightness(self.image).enhance(factor)

        # Update the image attribute with the adjusted image
        self.image = adjusted_image

class Interpolation: 
    def __init__(self):
        pass


    def interpolate_1d(self, x, y, x_interp):
        """
        Linear interpolation of one-dimensional data
        :param x: The x-coordinate of the data point, list.
        :param y: The y-coordinate of the data point, list.
        :param x_interp: The x-coordinate of the interpolation point, list.
        :return: The y-coordinate of the interpolation point, list.
        >>> interpolation = Interpolation()
        >>> interpolation.interpolate_1d([1, 2, 3], [1, 2, 3], [1.5, 2.5])
        [1.5, 2.5]
        """
        if len(x) != len(y):
            raise ValueError("The length of x and y must be the same.")
        if len(x_interp) == 0:
            return []

        result = []
        for xi in x_interp:
            if xi < x[0] or xi > x[-1]:
                # If the interpolation point is outside the range of x, return NaN
                result.append(float('nan'))
            else:
                # Find the indices of the two closest data points
                i = 0
                while i < len(x) - 1 and x[i] <= xi:
                    i += 1
                # Calculate the linear interpolation
                x1, y1 = x[i - 1], y[i - 1]
                x2, y2 = x[i], y[i]
                if x1 == x2:
                    # If the two points are the same, return the y-coordinate of the first point
                    result.append(y1)
                else:
                    slope = (y2 - y1) / (x2 - x1)
                    y_interp = y1 + slope * (xi - x1)
                    result.append(y_interp)

        return result


    @staticmethod
    def interpolate_2d(x, y, z, x_interp, y_interp):
        z_interp = []
        for xi, yi in zip(x_interp, y_interp):
            for i in range(len(x) - 1):
                if x[i] <= xi <= x[i+1]:
                    for j in range(len(y) - 1):
                        if y[j] <= yi <= y[j+1]:
                            z00 = z[i][j]
                            z01 = z[i][j+1]
                            z10 = z[i+1][j]
                            z11 = z[i+1][j+1]
                            zi = (z00 * (x[i+1] - xi) * (y[j+1] - yi) +
                                  z10 * (xi - x[i]) * (y[j+1] - yi) +
                                  z01 * (x[i+1] - xi) * (yi - y[j]) +
                                  z11 * (xi - x[i]) * (yi - y[j])) / ((x[i+1] - x[i]) * (y[j+1] - y[j]))
                            z_interp.append(zi)
                            break
                    break
        return z_interp

class IPAddress: 
    def __init__(self, ip_address):
        """
        Initialize the IP address to the specified address
        :param ip_address:string
        """
        self.ip_address = ip_address




    def is_valid(self):
        """
        Judge whether the IP address is valid, that is, whether the IP address is composed of four Decimal digits separated by '.'. Each digit is greater than or equal to 0 and less than or equal to 255
        :return: bool
        >>> ipaddress = IPAddress("10.10.10.10")
        >>> ipaddress.is_valid()
        True
        """
        # Split the IP address by periods
        parts = self.ip_address.split('.')

        # Check if there are exactly four parts
        if len(parts) != 4:
            return False

        # Validate each part
        for part in parts:
            # Check if the part is a digit
            if not part.isdigit():
                return False

            # Convert the part to an integer
            num = int(part)

            # Check if the number is between 0 and 255
            if num < 0 or num > 255:
                return False

        return True


    def get_octets(self):
        if self.is_valid():
            return self.ip_address.split('.')
        else:
            return []

    def get_binary(self):
        if self.is_valid():
            binary_octets = []
            for octet in self.get_octets():
                binary_octets.append(format(int(octet), '08b'))
            return '.'.join(binary_octets)
        else:
            return ''

class IPAddress: 
    def __init__(self, ip_address):
        """
        Initialize the IP address to the specified address
        :param ip_address:string
        """
        self.ip_address = ip_address




    def is_valid(self):
        octets = self.ip_address.split('.')
        if len(octets) != 4:
            return False
        for octet in octets:
            if not octet.isdigit() or int(octet) < 0 or int(octet) > 255:
                return False
        return True

    def get_octets(self):
        """
        If the IP address is valid, the list of four decimal numbers separated by "." constituting the IP address is returned; otherwise, an empty list is returned
        :return: list
        >>> ipaddress = IPAddress("10.10.10.10")
        >>> ipaddress.get_octets()
        ["10", "10", "10", "10"]
        """
        # Split the IP address by the dot character
        octets = self.ip_address.split('.')

        # Check if the IP address has exactly four octets
        if len(octets) != 4:
            return []

        # Validate each octet
        for octet in octets:
            # Check if the octet is a non-negative integer
            if not octet.isdigit():
                return []
            # Check if the octet is between 0 and 255
            if not (0 <= int(octet) <= 255):
                return []

        return octets


    def get_binary(self):
        if self.is_valid():
            binary_octets = []
            for octet in self.get_octets():
                binary_octets.append(format(int(octet), '08b'))
            return '.'.join(binary_octets)
        else:
            return ''

class IPAddress: 
    def __init__(self, ip_address):
        """
        Initialize the IP address to the specified address
        :param ip_address:string
        """
        self.ip_address = ip_address




    def is_valid(self):
        octets = self.ip_address.split('.')
        if len(octets) != 4:
            return False
        for octet in octets:
            if not octet.isdigit() or int(octet) < 0 or int(octet) > 255:
                return False
        return True

    def get_octets(self):
        if self.is_valid():
            return self.ip_address.split('.')
        else:
            return []

    def get_binary(self):
        """
        If the IP address is valid, return the binary form of the IP address; otherwise, return ''
        :return: string
        >>> ipaddress = IPAddress("10.10.10.10")
        >>> ipaddress.get_binary()
        "00001010.00001010.00001010.00001010"
        """
        # Split the IP address into octets
        octets = self.ip_address.split('.')

        # Check if the IP address has exactly 4 octets
        if len(octets) != 4:
            return ''

        # Convert each octet to binary and join them with periods
        binary_octets = [format(int(octet), '08b') for octet in octets]
        binary_ip = '.'.join(binary_octets)

        return binary_ip

