import numpy as np
class MetricsCalculator2: 
    def __init__(self):
        pass


    @staticmethod
    def mrr(data):
        """
        compute the MRR of the input data. MRR is a widely used evaluation index. It is the mean of reciprocal rank.
        :param data: the data must be a tuple, list 0,1,eg.([1,0,...],5).  In each tuple (actual result,ground truth num),ground truth num is the total ground num.
             ([1,0,...],5),
            or list of tuple eg. [([1,0,1,...],5),([1,0,...],6),([0,0,...],5)].
            1 stands for a correct answer, 0 stands for a wrong answer.
        :return: if input data is list, return the recall of this list. if the input data is list of list, return the
        average recall on all list. The second return value is a list of precision for each input.
        >>> MetricsCalculator2.mrr(([1, 0, 1, 0], 4))
        >>> MetricsCalculator2.mrr([([1, 0, 1, 0], 4), ([0, 1, 0, 1], 4)])
        1.0, [1.0]
        0.75, [1.0, 0.5]
        """
        if isinstance(data, list):
            # If data is a list of tuples, compute MRR for each tuple
            mrr_values = []
            precision_values = []
            for item in data:
                actual_result, ground_truth_num = item
                rank = 0
                for i, result in enumerate(actual_result):
                    if result == 1:
                        rank += 1
                        if rank == 1:
                            mrr_values.append(1.0 / rank)
                            precision_values.append(1.0)
                        else:
                            precision_values.append(rank / ground_truth_num)
                        break
                else:
                    mrr_values.append(0.0)
                    precision_values.append(0.0)
            return sum(mrr_values) / len(mrr_values), precision_values
        else:
            # If data is a single tuple, compute MRR for that tuple
            actual_result, ground_truth_num = data
            rank = 0
            for i, result in enumerate(actual_result):
                if result == 1:
                    rank += 1
                    if rank == 1:
                        return 1.0 / rank, [1.0]
                    else:
                        return rank / ground_truth_num, [rank / ground_truth_num]
            return 0.0, [0.0]


    @staticmethod
    def map(data):
        if type(data) != list and type(data) != tuple:
            raise Exception("the input must be a tuple([0,...,1,...],int) or a iteration of list of tuple")

        if len(data) == 0:
            return 0.0, [0.0]
        if type(data) == tuple:
            (sub_list, total_num) = data
            sub_list = np.array(sub_list)
            if total_num == 0:
                return 0.0, [0.0]
            else:
                ranking_array = 1.0 / (np.array(list(range(len(sub_list)))) + 1)

                right_ranking_list = []
                count = 1
                for t in sub_list:
                    if t == 0:
                        right_ranking_list.append(0)
                    else:
                        right_ranking_list.append(count)
                        count += 1

                ap = np.sum(np.array(right_ranking_list) * ranking_array) / total_num
                return ap, [ap]

        if type(data) == list:
            separate_result = []
            for (sub_list, total_num) in data:
                sub_list = np.array(sub_list)

                if total_num == 0:
                    ap = 0.0
                else:
                    ranking_array = 1.0 / (np.array(list(range(len(sub_list)))) + 1)

                    right_ranking_list = []
                    count = 1
                    for t in sub_list:
                        if t == 0:
                            right_ranking_list.append(0)
                        else:
                            right_ranking_list.append(count)
                            count += 1

                    ap = np.sum(np.array(right_ranking_list) * ranking_array) / total_num

                separate_result.append(ap)
            return np.mean(separate_result), separate_result

